L0014:	lda     _pad1
	and     #$04
	beq     L0015
;
; Snake1.y += 1;
;
	inc     _Snake1+1
;
; Snake1.head_sprite=SNAKE_HEAD_TILE_DOWN;
;
	lda     #$02
L000F:	sta     _Snake1+3
