	jmp     L0010
L0016:	lda     _pad2
	and     #$01
	beq     L0017
;
; mouse1.mouse_x += 1;
;
	inc     _mouse1
;
; mouse1.mouse_spr = MOUSE_RIGHT;
;
	lda     #$06
L0010:	sta     _mouse1+5
