; ---------------------------------------------------------------
; void __near__ draw_mouse (void)
; ---------------------------------------------------------------

.segment	"CODE"

.proc	_draw_mouse: near

.segment	"CODE"

;
; oam_spr(mouse1.mouse_x, mouse1.mouse_y, mouse1.mouse_spr,mouse1.sprite_att );
;
	jsr     decsp3
	lda     _mouse1
	ldy     #$02
	sta     (sp),y
	lda     _mouse1+1
	dey
	sta     (sp),y
	lda     _mouse1+5
	dey
	sta     (sp),y
	lda     _mouse1+6
	jsr     _oam_spr
;
; mouse1.mouse_x+=mouse1.mouse_dx;
;
	lda     _mouse1+2
	clc
	adc     _mouse1
	sta     _mouse1
;
; mouse1.mouse_y+=mouse1.mouse_dy;
;
	lda     _mouse1+3
	clc
	adc     _mouse1+1
	sta     _mouse1+1
;
; if(mouse1.mouse_x>=(256-8)) mouse1.mouse_dx=-mouse1.mouse_dx;
;
	lda     _mouse1
	cmp     #$F8
	bcc     L0004
	lda     _mouse1+2
	eor     #$FF
	clc
	adc     #$01
	sta     _mouse1+2
;
; if(mouse1.mouse_y>=(240-8)) mouse1.mouse_dy=-mouse1.mouse_dy;
;
L0004:	lda     _mouse1+1
	cmp     #$E8
	bcc     L0003
	lda     _mouse1+3
	eor     #$FF
	clc
	adc     #$01
	sta     _mouse1+3
;
; }
;
L0003:	rts

.endproc

; ---------------------------------------------------------------
; void __near__ mouse_coord (void)
; ---------------------------------------------------------------

.segment	"CODE"

.proc	_mouse_coord: near

.segment	"CODE"

;
; mouse1.mouse_spr=MOUSE_LEFT;
;
	lda     #$07
	sta     _mouse1+5
;
; mouse1.mouse_x=rand8();
;
	jsr     _rand8
	sta     _mouse1
;
; mouse1.mouse_y=rand8();
;
	jsr     _rand8
	sta     _mouse1+1
;
; j=rand8();
;
	jsr     _rand8
	sta     _j
;
; spr=1+(rand8()%3);
;
	jsr     _rand8
	jsr     pushax
	lda     #$03
	jsr     tosumoda0
	clc
	adc     #$01
	sta     _spr
;
; mouse1.mouse_dx=j&1?-spr:spr;
;
	lda     _j
	and     #$01
	beq     L0008
	lda     _spr
	eor     #$FF
	clc
	adc     #$01
	jmp     L0009
L0008:	lda     _spr
L0009:	sta     _mouse1+2
;
; spr=1+(rand8()%3);
;
	jsr     _rand8
	jsr     pushax
	lda     #$03
	jsr     tosumoda0
	clc
	adc     #$01
	sta     _spr
;
; mouse1.mouse_dy=j&2?-spr:spr;
;
	lda     _j
	and     #$02
	beq     L000A
	lda     _spr
	eor     #$FF
	clc
	adc     #$01
	jmp     L000B
L000A:	lda     _spr
L000B:	sta     _mouse1+3
;
; }
;
	rts

.endproc

